INSERT INTO "public"."test_questions" ("id", "test_id", "question", "question_type", "order", "points", "created_at", "updated_at", "correct_order") VALUES ('863847fb-76a1-4f62-998c-2df8150e36a0', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Какое действие необходимо предпринять ТП, чтобы не допустить возникновения OOS в торговой точке?', 'single_choice', '0', '10', '2025-07-14 13:15:09.641692+00', '2025-07-14 14:19:10.301804+00', null), ('8bd1669f-27ac-4b35-8378-f1250d0a6198', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Решите кейс: ТП сформировал с клиентом заявку по ВАТ, а когда перешел к формированию заказа по напиткам, клиент возразил: «У меня нет денег на напитки». Какой уточняющий вопрос поможет ТП выяснить «истинность/ложность» данного возражения клиента?', 'single_choice', '0', '10', '2025-07-14 13:21:09.794614+00', '2025-07-14 14:19:12.31884+00', null), ('8c8d1071-18f2-46fc-9a5a-ad604863f6c2', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Решите кейс: Как у вас продаются безалкогольные газированные напитки? А какие по цене напитки пользуются спросом среди покупателей? Я правильно понимаю, что вы готовы торговать недорогими, но качественными напитками?', 'single_choice', '0', '10', '2025-07-14 13:21:45.626535+00', '2025-07-14 14:19:13.61865+00', null), ('8ce5ea7c-54c4-4b65-9757-91af9650c936', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Какая фраза, произнесенная ТП при завершении сделки, поможет «дожать» клиента и совершить дополнительную продажу?', 'single_choice', '0', '10', '2025-07-14 13:21:48.528433+00', '2025-07-14 14:19:09.527509+00', null), ('93a11d6d-cbc4-43c9-a6b6-a21179cf9be9', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Решите кейс: ТП в ходе визита в ТТ сталкивается с возражением клиента. Какой вопрос поможет ТП определить, что данное возражение является «первостепенным»?', 'single_choice', '0', '10', '2025-07-14 13:20:37.069141+00', '2025-07-14 14:19:11.697617+00', null), ('9e95ea1b-6ba1-4c3a-b790-58fdc6a7cab4', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Какова правильная последовательность действий ТП при работе с возражениями клиента?', 'sequence', '2', '10', '2025-07-14 13:24:29.262557+00', '2025-07-14 14:19:15.290904+00', null), ('c2027f8d-6eda-4d74-99a6-21dc1b9ff509', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Решите кейс: В ходе визита ТП в ТТ ЛПР в разговоре с ним несколько раз произносит фразу - «для меня важны натуральный состав и качество товара». Какой фразой ТП может подстроиться к клиенту по ключевым словам?', 'single_choice', '0', '10', '2025-07-14 13:21:12.631728+00', '2025-07-14 14:19:12.922528+00', null), ('cd6f3255-3b89-4afb-8bb1-4c81496f112a', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Решите кейс: в ходе визита в ТТ ТП замечает, что ЛПР разговаривает с клиентами громким голосом и быстро произносит слова. Как ТП необходимо изменить особенности своей речи, чтобы лучше установить контакт с ЛПР?', 'single_choice', '0', '10', '2025-07-14 13:20:10.259389+00', '2025-07-14 14:19:11.058407+00', null), ('e0eef325-fe28-4094-95c3-6a1592317da4', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Для эффективной презентации продукции ГК «СНС» ТП может использовать метод «Язык выгод». В какой последовательности ему нужно презентовать товар согласно методу «Язык выгод»?', 'sequence', '3', '10', '2025-07-14 13:24:29.848996+00', '2025-07-14 14:19:16.438561+00', null), ('e438562b-68d5-4cf1-810b-a55215dbb57c', 'c846511a-6518-4dd4-b7ee-b6a6cdbed519', 'Учёт какой личной потребности продавца в первую очередь поможет ТП увеличить «стандартную» заявку по напиткам?', 'single_choice', '1', '10', '2025-07-14 13:05:03.749908+00', '2025-07-14 14:19:14.267638+00', null);